import React, { Component } from 'react'

import ReactPlayer from 'react-player';
import ProgressBar from './ProgressBar';

export default class YoutubeFooter extends Component {

    state = {
        playing : false,
        progress : 0.0
    }

    togglePlaying = () => {
        this.setState({playing: !this.state.playing})
    }

    progressBarDragged = (x) => {
        console.log((x - 300) / 500)
        this.setState({progress: (x - 300) / 500})
    }

    render() {
        var {title, author, url} = this.props
        var {progress, playing} = this.state
        return (
            <div style = {{
                height: 200, 
                width: '100%', 
                backgroundColor: '#b5b5b5', 
                position: 'fixed', 
                bottom: 0,
                display: 'flex',
                flexDirection: 'row'}}>

                <ReactPlayer 
                    url={url}
                    width = {200}
                    height = {200}
                    playing = {playing}
                    onReady = {() => console.log("Ready")}/>

                <div style = {{padding: 10}}>
                    <h5>{title}</h5>
                    <h6>{author}</h6>
                </div>
                <div style = {{display: 'flex', flexDirection: 'column', alignItems: 'center',position: 'relative', top: -7.6, width: 500}}>   
                    <ProgressBar 
                            width = {500} 
                            height = {30} 
                            progress = {progress}
                            completedBarColor = "#5e5e5e"
                            notCompletedBarColor = "#8f8f8f"
                            cursorColor = "#fff"
                            render = {(x) => this.progressBarDragged(x)}/>
                    <div style = {{
                        display: 'flex', 
                        flexDirection: 'row', 
                        justifyContent: 'space-between', 
                        alignItems: 'center', 
                        width: '100%'}}>
                        <i onClick = {this.togglePlaying} style = {{fontSize: 100}} class="material-icons">play_arrow</i>
                        {/* <ProgressBar 
                            width = {100} 
                            height = {20} 
                            progress = {0.2}
                            completedBarColor = "#5e5e5e"
                            notCompletedBarColor = "#8f8f8f"
                            cursorColor = "#fff"/> */}
                    </div>
                </div>

            </div>
        )
    }
}
