import React, { Component } from 'react'

import { Modal } from 'react-materialize';

export default class MerchCard extends Component {

    state = {
        sizeColors : ['#9fa8da', '#9fa8da', '#9fa8da', '#9fa8da', '#9fa8da']
    }

    toggle = (position) => {
        var newColors = ['#9fa8da', '#9fa8da', '#9fa8da', '#9fa8da', '#9fa8da']
        this.state.sizeColors.forEach((element, index) => {
            if(index === position){
                newColors[index] = element == '#5c6bc0' ? '#9fa8da' : '#5c6bc0'
            }
        })
        this.setState({sizeColors: newColors})
    }
    render() {
        var {imgUrl, title, price} = this.props
        var {sizeColors} = this.state
        return (
            <div>
                <Modal header="Modal Header" className ="Merch-Modal" trigger={
                    <div class="col s10">
                        <div class="card z-depth-3">
                            <div class="card-image">
                                <img src={imgUrl}/>
                            </div>
                            <div style = {{display: 'flex', flexDirection: 'row', alignItems: 'center', justifyContent: 'space-between', padding: 20}}>
                                <span class="card-title">{title}</span>
                                <h5>{price}</h5>
                            </div>
                        </div>
                    </div>}>

                    <div style = {{display: 'flex', flexDirection: 'row'}}>
                        <img style = {{height: 400}} src={imgUrl}/>
                        <div>
                            <a style ={{margin: 5, backgroundColor: sizeColors[0]}} class="waves-effectbtn" onClick = {() => this.toggle(0)}>small</a>
                            <a style ={{margin: 5, backgroundColor: sizeColors[1]}} class="waves-effectbtn" onClick = {() => this.toggle(1)}>medium</a>
                            <a style ={{margin: 5, backgroundColor: sizeColors[2]}} class="waves-effectbtn" onClick = {() => this.toggle(2)}>large</a>
                            <a style ={{margin: 5, backgroundColor: sizeColors[3]}} class="waves-effectbtn" onClick = {() => this.toggle(3)}>x-large</a>
                            <a style ={{margin: 5, backgroundColor: sizeColors[4]}} class="waves-effectbtn" onClick = {() => this.toggle(4)}>xx-large</a>
                        </div>
                    </div>
                </Modal>
            </div>

        )
    }
}
