import React, { Component } from 'react'

export default class ProgressBar extends Component {

    state = {
        dragging: false,
        dragOrigin: null,
        dragCoordinates: null,
        progress : 0.6
    }

    cursorDragStart = (e) => {
        this.setState({dragging: true, dragOrigin: {x: e.clientX, y: e.clientY}})
    }

    cursorDragging = (e) => {
        if(this.state.dragging){
            this.setState({x: e.clientX, y: e.clientY})
        }
    }

    cursorDragEnd = () => {
        this.setState({dragging: false})
    }

    render() {
        var {width, height, completedBarColor, notCompletedBarColor, cursorColor} = this.props
        var circleRadius = height / 2
        var barHeight = 15
        var barOffset = (height - barHeight)/2
        var completedBarWidth = width * this.state.progress
        var notCompletedBarWidth = width - completedBarWidth
        var cursorOffset = completedBarWidth

        if(this.state.dragCoordinates){
            var {x, y} = this.state.dragCoordinates
        } else {
            var x = 0
            var y = 0
        }

        return (
            <div style = {{
                display: 'flex', 
                flexDirection: 'row', 
                height: height, 
                width: width,
                alignItems: 'center'}}>

                <svg width = {width} height={height}>
                    <rect x = "0" y={barOffset} fill={completedBarColor} width={completedBarWidth} height={barHeight}/>
                    <rect x={completedBarWidth} y={barOffset} fill={notCompletedBarColor} width={notCompletedBarWidth} height={barHeight}/>
                    <circle 
                        transform={`translate(${x}, ${y})`}
                        onMouseDown = {this.cursorDragStart} 
                        onMouseMove = {this.cursorDragging}
                        cx={cursorOffset} cy={circleRadius} r={circleRadius} fill={cursorColor} />
                </svg>
            </div>
        )
    }
}
